/*global tinymce:true */

tinymce.PluginManager.add('panel', function(editor) {
	editor.addCommand('InsertPanel', function() {
		editor.execCommand('mceInsertContent', false, 'PANEL');
	});

	editor.addButton('panel', {
		icon: false,
		text:'some text',
		tooltip: 'Horizontal line',
		cmd: 'InsertPanel'
	});

	editor.addMenuItem('hr', {
		icon: 'hr',
		text: 'Horizontal line',
		cmd: 'InsertPanel',
		context: 'insert'
	});
});

